pragma solidity >=0.4.21 <0.7.0;

contract CrowdFunding {
    string public name;     //state variable its value is stored in the blockC. and not in memory
    mapping (uint => Post) public posts;
    uint public postCount = 0;

    struct Post {
        uint id;
        string content;
        uint tipAmount;
        address payable author;
    }

    event PostCreated(
        uint id,
        string content,
        uint tipAmount,
        address payable author
    );

    event PostTipped(
        uint id,
        string content,
        uint tipAmount,
        address payable author
    );

    constructor() public {
        name = "Satyam";
    }

    function createPost(string memory _content) public {
        //Check if content is empty or not
        require(bytes(_content).length > 0,'Error');
        //Increment Post Count
        postCount++;
        //Create Post and do the mapping
        posts[postCount] = Post(postCount,_content,0,msg.sender);   //This sets the value
        emit PostCreated(postCount,_content,0,msg.sender);  //This puts the value in the BlockC.
    }

    function tipPost(uint _id) public payable{
        require(_id > 0 && _id <= postCount,'Invalid ID');
        //Fetch the post
        //A copy is stored in memory so it will not affect the data on BlockC
        Post memory _post = posts[_id];
        //Fetch author
        address payable _author = _post.author;
        //Pay author
        //address(_author).transfer(msg.value);
        _author.transfer(msg.value);
        //Increment Tip amount
        _post.tipAmount = _post.tipAmount + msg.value;
        //Update post
        posts[_id] = _post;
        //Trigger an Event
        emit PostTipped(postCount,_post.content,_post.tipAmount,_author);
    }
}
